using namespace std;
typedef long long int uli;
string w="aeiou";
bool vow(char x){
  for(char ch:w)if(x==ch)return true;
  return false;
}
int main(){
  string s;
  cin>>s;
  int n=s.size();
  bool ok=true;
  for(int i=0;i<n;i++){
    if(!vow(s[i]) && s[i]!='n'){
      if(i+1>=n || !vow(s[i+1])){
        ok=false;
      }
    }
  }
  if(ok)puts("YES");
