#include <iostream>
using namespace std;

int main() {
    int n, v;
    cin >> n >> v;
    int act = 0, sol = 0;
    for (int i = 1; i <= n; i++) {
        while(act < n-i && act < v) {
            act++; sol += i;
        }
        act--;
    }
    cout << sol << endl;
    ios_base::sync_with_stdio(false);
    return 0;
}
