#include <bits/stdc++.h>
using namespace std;

int main()
{
    string s;
    int n, m, a, b;
    char c1, c2;

    cin >> n >> m;
    cin >> s;

    while (m--) {
        cin >> a >> b >> c1 >> c2;
        for (int i(a - 1); i <= b - 1; i++)
            if (s[i] == c1)
                s[i] = c2;
    }

    cout << s;

    return 0;
}
