#include "bits/stdc++.h"

using namespace std;

using ll = long long;

void solve() {
    int n; cin >> n;
    vector<int> a(n);
    for(auto &x : a) cin >> x;
    for(int i = 0; i < n; ++i) {
        for(int j = i + 1; j < n; ++j) {
            if (gcd(a[i], a[j]) <= 2) {
                cout << "YES\n";
                return;
            }
        }
    }
    cout << "NO\n";
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    int t = 1;
    cin >> t;

    while (t--) {
        solve();
    }

    return 0;
}
