#include <cstdio>
int a[100000];
int main()
{
	int n;
	scanf("%d", &n);
	for (int i = 0; i < n; ++i)
		scanf("%d", &a[i]);
	int m;
	scanf("%d", &m);
	while (m--)
	{
		int x, y;
		scanf("%d%d", &x, &y);
		--x; --y;
		if (x)
			a[x - 1] += y;
		if (x + 1 < n)
			a[x + 1] += a[x] - y - 1;
		a[x] = 0;
	}
	for (int i = 0; i < n; ++i)
		printf("%d\n", a[i]);
	return 0;
}
