#include <cstdio>
#include <algorithm>

using namespace std;

int main() {
    freopen("input.txt", "r", stdin);
    freopen("output.txt", "w", stdout);
    int cup;
    bool p[] = {false, false, false};
    scanf("%d", &cup);
    p[cup-1] = true;
    for(int i=0; i<3; i++){
        int a, b;
        scanf("%d%d", &a, &b); a--; b--;
        swap(p[a], p[b]);
    }
    for(int i=0; i<3; i++){
        if (p[i]) {
            printf("%d\n", i+1);
            break;
        }
    }
    return 0;
}
