#include <cstdio>
#include <cstring>
#include <algorithm>
#include <queue>
#include <map>
#include <vector>

using namespace std;

int a[ 100001 ];

int main( )
{
	int n, k, i, j;
	scanf("%d%d", &n, &k);
	for ( i = 0; i < n; i++ )
		scanf("%d", &a[ i ]);
	for ( i = k; i < n; i++ )
		if ( a[ i ] != a[ k - 1 ] )
			break;
	if ( i < n )
		puts("-1");
	else
	{
		for ( i = k - 2; i >= 0; i-- )
			if ( a[ i ] != a[ k - 1 ] )
				break;
		printf("%d\n", i + 1);
	}
	return 0;
}
