#include<bits/stdc++.h>
using namespace std;
int getsum(int val){
	int res=0;
	while(val){
		res+=val%10;
		val/=10;
	}
	return res;
}
void solve(){
	int N,K;cin>>N>>K;
	for(int i=N;;++i)if(getsum(i)%K==0){cout<<i<<endl;return;}
}
int main(){
	int t;cin>>t;
	while(t--)solve(); 
    return 0;
}
