using namespace std;

map <string, int> r;

char s[105], t[105];

vector <pair <int, string> > v;

int main (void)
{
  r["rat"] = 1, r["woman"] = 2, r["child"] = 2, r["man"]=3, r["captain"]=4;

  int n;
  scanf("%d", &n);
  for (int i=0; i<n; i++) {
    scanf("%s%s", s, t);
    v.push_back(make_pair(r[t] * 10000 + i, s));
  }

  sort(v.begin(), v.end());

  for (int i=0; i<n; i++)
    puts(v[i].second.c_str());

  return 0;
}
