#include<bits/stdc++.h>

using namespace std;

typedef pair<int, int> PA;
typedef long long LL;

#define MAXN 200003
#define MODP 1000000007
#define FST first
#define SCD second


LL qpow(LL x, int y) {
	LL res = 1;
	for (;y>0;y>>=1, x = x*x%MODP) {
		if (y&1) res = (res*x)%MODP;
	}
	return res;
}

int q[200];

int main() {
	#ifndef ONLINE_JUDGE
	freopen("in.txt", "r", stdin);
	#endif
	int Tn = 1;
	// scanf("%d", &Tn);
	for (int ci=1;ci<=Tn;++ci) {
		int n, d;
		cin>>n>>d;
		for (int i=0;i<n;++i) {
			scanf("%d", q+i);
		}
		int ans = 0;
		set<int > ss;
		ss.clear();
		for (int i=0;i<n;++i) {
			for (int j=-d;j<=d;j+=d) {
				if (j) {
					int x = q[i]+j;
					if (ss.count(x)) continue;
					int flag = 0;
					for (int k=0;k<n;++k) {
						if (abs(q[k]-x)<d) flag = 1;
					}
					if (!flag) {
						ss.insert(x);
						ans+=1;
					}
				}
			}
		}
		cout << ans << endl;
	}
	return 0;
}
