#include <iostream>
#include <string>
using namespace std;
string a[10]={
"O-|-OOOO",
"O-|O-OOO",
"O-|OO-OO",
"O-|OOO-O",
"O-|OOOO-",
"-O|-OOOO",
"-O|O-OOO",
"-O|OO-OO",
"-O|OOO-O",
"-O|OOOO-"};
int main(){
    int n;
    cin>>n;
    if (n==0){
        cout<<a[0]; return 0;
    }
    while (n>0){
        cout<< a[n%10];
        n=n/10;
        if (n>0) cout<<"\n";
    }
    return 0;
}
