#include <iostream>
#include <cstdio>
#include <cmath>
#include <cstring>
#include <vector>
#include <algorithm>
#include <cstdlib>
#include <string>
#include <queue>
#include <map>
#include <set>

#define MAX 1024

using namespace std;
FILE *in; FILE *out;

int n;
int a[MAX][2];

int main(void)
{
	in = stdin; out = stdout;
//	in = fopen("A.in", "rt"); out = fopen("A.out", "wt");

	fscanf(in, "%d", &n);
	for (int i = 0; i < n; i++)
		fscanf(in, "%d %d", &a[i][0], &a[i][1]);

	int ans = 0;
	for (int i = 0; i < n; i++)
		for (int c = 0; c < n; c++)
			if (a[i][0] + a[i][1] == a[c][0])
				if (a[c][0] + a[c][1] == a[i][0])
					ans = 1;
	fprintf(out, "%s\n", ans ? "YES" : "NO");

	return 0;
}
