typedef long long ll;
int n,m,T,k;
char s[105];
int main(){
	cin>>T;
	while (T--){
		cin>>n>>k;
		scanf("%s",s+1);
		if (k*2==n){
			puts("NO");
			continue;
		}
		int flag=0;
		for (int i=1;i<=k;i++)
			if (s[i]!=s[n-i+1]){
				puts("NO");
				flag=1;
				break;
			}
		if (!flag)puts("YES");
	}
}
