#include <bits/stdc++.h>
using namespace std;
#define int long long
#define IOS ios::sync_with_stdio(0) , cin.tie(0) , cout.tie(0)
inline int in(){int32_t x ; scanf("%d",&x) ; return x ;}
inline long long lin(){long long x ; scanf("%lld",&x) ; return x;}
const int maxn = 1e6 + 200;
const int mod = 1e9 + 7;
const long long infll = 2e18 + 10;
const int inf = 1e9 + 10;
typedef pair<int,int> pii;
typedef long long ll;
typedef vector<int> vi;
//inline int shit(double avg) {int x = (int)(avg + 0.5); return x ;}
int32_t main()
{
    int n = in() , k = in();
    int sum = 0 ;
    for (int i = 0 ; i < n ; i ++) sum += in();
    int low = -1 , hi = 1e15;
    while(low + 1 < hi)
    {
        int mid = (low + hi ) >> 1;
        int tot = sum + mid * k;
        double avg = (double)tot/(n + mid);
        int x = (int)(avg + 0.5);
        if (x >= k )
            hi = mid;
        else low = mid;
    }
    cout << hi << "\n";
}
