inline int read(){
    int x=0,f=0;char ch=getchar();
    while(!isdigit(ch))f|=ch=='-',ch=getchar();
    while(isdigit(ch))x=10*x+ch-'0',ch=getchar();
    return f?-x:x;
}
template<typename T>void print(T x){
    if(x<0)putchar('-'),x=-x;
    if(x>=10)print(x/10);
    putchar(x%10+'0');
}
template<typename T>void print(T x,char ch){
    print(x),putchar(ch);
}

void solve(){
    string a;
    cin>>a;
    if(SZ(a)%2==0){
        int ans=0;
        for(int i=0;i<SZ(a);i++)ans+=a[i]-'a'+1;
        printf("Alice %d\n",ans);
    }else{
        int k1=0,k2=0;
        for(int i=0;i<SZ(a)-1;i++)k1+=a[i]-'a'+1;
        k1-=a[SZ(a)-1]-'a'+1;
        for(int i=1;i<SZ(a);i++)k2+=a[i]-'a'+1;
        k2-=a[0]-'a'+1;
        if(k1<k2)swap(k1,k2);
        if(k1>0)printf("Alice %d\n",k1);
        else printf("Bob %d\n",-k1);
    }
}

int main(){
    int T=read();
    while(T--)solve();
    return 0;
}
