using namespace std;

#define IOS ios::sync_with_stdio(0); cin.tie(0); cout.tie(0);
#define endl "\n"
#define ll long long

int32_t main()
{
	IOS;
	string s, t;
	cin >> s >> t;
	int n=s.size();
