#include <bits/stdc++.h>
#define ld long double
#define ll long long int
#define st first
#define nd second
#define pb push_back
#define pii pair<int,int>
#define mp make_pair

using namespace std;


const int N = 55;
ll a[N];
int n;

void solve(){
    ll mx = 0;
    cin >> n;
    for(int i=1;i<=n;i++){
        cin >> a[i];
        if(i >= 2 && a[i - 1] > a[i]) mx = max(mx, a[i - 1]);
    }
    cout << mx << "\n";
}

int main(){
    ios_base::sync_with_stdio(0); cin.tie(0);

    int tt = 1;
    cin >> tt;
    while(tt--) solve();

    return 0;
}
