#include <bits/stdc++.h>
using namespace std;
int n, a[2020], b[2020];
int v[10000020];
int main() {
	scanf("%d", &n);
	for (int i = 0; i < n; i++) {
		scanf("%d", &a[i]);
		v[a[i]] = 1;
	}
	for (int i = 0; i < n; i++) {
		scanf("%d", &b[i]);
		v[b[i]] = 1;
	}
	int cnt = 0;
	for (int i = 0; i < n; i++) {
		for (int j = 0; j < n; j++) {
			if (v[a[i] ^ b[j]]) {
				cnt++;
			}
		}
	}
	printf(cnt % 2 ? "Koyomi\n" : "Karen\n");
	return 0;
}
