#include <bits/stdc++.h>

using namespace std;

using ll = long long;
using vi = vector<int>;
using db = double;
#define pb push_back
#define fir first
#define sec second

int dof(string a) {
	if (a == "^") return 0;
	if (a == ">") return 1;
	if (a == "v") return 2;
	if (a == "<") return 3;
}

int main() {
	string a, b;
	cin >> a >> b;
	ll n;
	cin >> n;
	bool cw = (dof(a) + n) % 4 == dof(b);
	bool ccw = (dof(a) + n * 3) % 4 == dof(b);

	puts(cw && ccw ? "undefined" : cw ? "cw" : "ccw");

	return 0;
}
