using namespace std;

int main()
{
  int i,a,b,c,tmp,tot;
  scanf("%d%d%d",&a,&b,&c);
  tot=0;
  tmp=a*2;
  for (i=0;i<b+c-1;i++)
  {
    tot+=(tmp/2);
    if (i+1<b) tmp++;
    else tmp--;
    if (i+1<c) tmp++;
    else tmp--;
  } 
  printf("%d\n",tot);
}
