#include<bits/stdc++.h>
using namespace std;
#define LL long long
#define pa pair<int,int>
const int N=111;
LL read()
{
	LL x=0,f=1;char ch=getchar();
	while(ch<'0'||ch>'9'){if(ch=='-')f=-1;ch=getchar();}
	while(ch>='0'&&ch<='9')x=x*10ll+ch-'0',ch=getchar();
	return x*f;
}
int n,c[N];
void solve()
{
	n=read();
	memset(c,0,sizeof(c));
	for(int i=1;i<=n;i++)c[read()]++;
	int ans=0,p=-1;
	for(int i=0;i<=100;i++)
	if(c[i]>1)
	{
		p=i;
		break;
	}
	if(c[0])printf("%d\n",n-c[0]);
	else if(p!=-1)printf("%d\n",n);
	else printf("%d\n",n+1);
}
int main()
{
	int T=read();
	while(T--)solve();
 	return 0;
}
