#include <bits/stdc++.h>
#define ld long double
#define ll long long int
#define st first
#define nd second
#define pb push_back
#define pii pair<int,int>
#define mp make_pair

using namespace std;

const int N = 105;
int a[N];
int n;

void solve(){
    cin >> n;
    for(int i=1;i<=n;i++) cin >> a[i];

    for(int c=0;c<=n;c++){
        int cnt = 0;
        for(int i=1;i<=n;i++){
            cnt += (a[i] > c);
        }
        if(cnt == c){
            cout << c << "\n";
            return;
        }
    }
    cout << -1 << "\n";

}

int main(){
    ios_base::sync_with_stdio(0); cin.tie(0);

    int tt = 1;
    cin >> tt;
    while(tt--) solve();

    return 0;
}
