#include <bits/stdc++.h>
using namespace std;

template < typename T >
inline const void read(T &x)
{
	bool flg = x = 0;
	char c;
	while ((c = getchar()) < 48 || c > 57)
		flg |= c == '-';
	do
		x = (x << 1) + (x << 3) + (c ^ 48);
	while ((c = getchar()) > 47 && c < 58);
	if (flg) x = -x;
}

int OUTPUT[45];

template < typename T >
inline const void write(T x)
{
	if (x < 0)
	{
		putchar('-');
		x = -x;
	}
	int len = 0;
	do
	{
		OUTPUT[++len] = x % 10 | 48;
		x /= 10;
	}while (x);
	while (len)
		putchar(OUTPUT[len--]);
}

template < typename T >
inline const void writesp(const T x)
{
	write(x);
	putchar(32);
}

template < typename T >
inline const void writeln(const T x)
{
	write(x);
	putchar(10);
}

const int N = 1e5 + 5;
int T, n, a[N];

int main()
{
//	freopen(".in", "r", stdin);
//	freopen(".out", "w", stdout);
	read(T);
	while (T--)
	{
		read(n);
		for (int i = 1; i <= n; ++i)
			read(a[i]);
		for (int i = 1; i <= n; ++i)
			writesp(n - a[i] + 1);
		putchar(10);
	}
	return 0;
}
