#include <cstdio>
#include <cstring>
#include <algorithm>
#include <iostream>
#include <climits>
#include <numeric>
#define foreach(e,x) for(__typeof(x.begin()) e=x.begin();e!=x.end();++e)
#define REP(i,n) for(int i=0;i<n;++i)
using namespace std;

int main() {
	int n;
	cin >> n;
	int dp[12000];
	fill(dp, dp + 12000, INT_MAX / 2);
	dp[0] = 0;
	int sum = 0;
	for (int i = 0; i < n; ++i) {
		int a;
		cin >> a;
		sum += a;
		for (int j = 12000 - 1; j >= 0; --j) {
			if (j + a < 12000)
				dp[j + a] = min(dp[j + a], dp[j] + 1);
		}
	}
	int ans = INT_MAX / 2;
	for (int i = 0; i < 12000; ++i) {
		if (i + i > sum)
			ans = min(ans, dp[i]);
	}
	cout << ans << endl;
	return 0;
}
