#include <iostream>

using namespace std;

int n, k;
int a[100];

long long calc() {
    long long x = 0;
    for (int i = 0; i < n; i++) {
        x = x * k + a[i];
    }
    return x;
}
int main() {
    cin >> n >> k;
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }
    long long x = calc();
    cin >> n >> k;
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }
    long long y = calc();
    if (x == y) {
        cout << '=' << endl;
    } else if (x < y) {
        cout << '<' << endl;
    } else {
        cout << '>' << endl;
    }
    return 0;
}
