#include<bits/stdc++.h>
using namespace std;
typedef long long LL;
typedef double DB;
char buf[1<<21],*p1=buf,*p2=buf;
#define getchar() (p1==p2 && (p2=(p1=buf)+fread(buf,1,1<<18,stdin),p1==p2)?EOF:*p1++)
LL read()
{
	LL x=0,f=1;
	char c=getchar();
	while(c<'0' || c>'9')	f=(c=='-'?-1:f),c=getchar();
	while(c>='0' && c<='9')	x=(x<<1)+(x<<3)+(c^'0'),c=getchar();
	return x*f;
}
void write(LL x)
{
	if(x>9)	write(x/10);
	putchar(x%10+'0');
}
void Solve();
int main(){
	LL T=read();
	while(T-->0)	Solve();
	return 0;
}
LL n,a[100005];
void Solve()
{
	n=read();
	for(LL i=1;i<=n;++i)	a[i]=read();
	LL p=0,q=0;
	for(LL i=1;i<=n;++i)
	{
		if(a[i]<0)	p-=a[i];
		else	q+=a[i];
	}
	write(max(p,q)-min(p,q)),puts("");
}
