	for(int i=1;i<=t;i++)
	{
		sa[i]=a-(i-1)*da;
		sb[i]=b-(i-1)*db;
	}
	int ans=0;
	sa[0]=0;sb[0]=0;
	for(int i=0;i<=t;i++)
	for(int j=0;j<=t;j++)
	{
		if(sa[i]+sb[j]==x)
