#include <cstdio>
#include <iostream>
#include <cstring>
#include <algorithm>
#include <vector>
#include <deque>

using namespace std;

string s;

bool check()
{
	int t = s.find("AB");
	if (t != string::npos) {
		s.replace(t, 2, "--");
		if (s.find("BA") != string::npos)
			return true;
		s.replace(t, 2, "AB");
	}

	t = s.find("BA");
	if (t != string::npos) {
		s.replace(t, 2, "--");
		if (s.find("AB") != string::npos)
			return true;
	}

	return false;
}

void EXEC()
{
	cin >> s;
	if (check())
		printf("YES\n");
	else
		printf("NO\n");
}

int main()
{
	EXEC();
	return 0;
}
