#include <stdio.h>
#include <string.h>
#include <iostream>
#include <algorithm>
#include <vector>
#include <queue>
#include <set>
#include <map>
#include <string>
#include <math.h>
#include <stdlib.h>
#include <time.h>
using namespace std;
int a[100];

int main()
{
    //freopen("in.txt","r",stdin);
    //freopen("out.txt","w",stdout);
    int n,v;
	while(scanf("%d%d",&n,&v) == 2)
	{
		vector<int>ans;
		for(int i = 1;i <= n;i++)
		{
			int k;
			scanf("%d",&k);
			for(int j = 0;j < k;j++)scanf("%d",&a[j]);
			for(int j = 0;j < k;j++)
				if(a[j] < v)
				{
					ans.push_back(i);
					break;
				}
		}
		int sz = ans.size();
		printf("%d\n",sz);
		for(int i = 0;i < sz;i++)
		{
			printf("%d",ans[i]);
			if(i < sz-1)printf(" ");
			else printf("\n");
		}
	}
    return 0;
}
