#include <stdio.h>
#include <stdlib.h>
#include <iostream>
#include <algorithm>

using namespace std;

int x, y, x1, y1, x2, y2;

int main()
{
	scanf("%d %d", &x, &y);
	if (x > 0)
	{
		if (y > 0)
		{
			x1 = x + y, y1 = 0;
			x2 = 0, y2 = x + y;
		}

		else
		{
			x1 = x - y, y1 = 0;
			x2 = 0, y2 = 0 - (x - y);
		}
	}

	else
	{
		if (y < 0)
		{
			x1 = x + y, y1 = 0;
			x2 = 0, y2 = x + y;
		}

		else
		{
			x1 = x - y, y1 = 0;
			x2 = 0, y2 = 0 - (x - y);
		}
	}

	if (x1 > x2)
		swap(x1, x2), swap(y1, y2);
	printf("%d %d %d %d\n", x1, y1, x2, y2);
	return 0;
}
