#include <stdio.h>
int main()
{
    int n,m;
    while (scanf("%d%d",&n,&m)==2)
    {
        if (n==0&&m!=0) puts("Impossible");
        else if (n==0&&m==0) puts("0 0");
        else if (n!=0&&m==0)
        {
            printf("%d %d\n",n,n);
        }
        else
        {
            if (m<=n)
            {
                printf("%d %d\n",n,m+n-1);
            }
            else
            {
                printf("%d %d\n",m,m+n-1);
            }
        }
    }
    return 0;
}
