using namespace std;

int a[100];

int main() {
    int n;
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    if (a[n - 1] == 15 || a[n - 1] == 0) {
        if (a[n - 1] == 15) {
            puts("DOWN");
        } else{
            puts("UP");
        }
    } else {
        if (n == 1) {
            puts("-1");
        } else {
            if (a[n - 2] < a[n - 1]) {
                puts("UP");
            } else {
                puts("DOWN");
            }
        }
    }
    return 0;
}
