// Problem: A. Walking Master
// Contest: Codeforces - Codeforces Round 858 (Div. 2)
// URL: https://codeforces.com/contest/1806/problem/A
// Memory Limit: 1024 MB
// Time Limit: 1000 ms

#include<bits/stdc++.h>
#define poly vector<int>
#define IOS ios::sync_with_stdio(false)
#define ll long long
#define mp make_pair
#define mt make_tuple
#define pa pair < int,int >
#define fi first
#define se second
#define inf 1e18
#define mod 998244353
#define int ll
// #define N 
using namespace std;
void BellaKira()
{
	int a,b,c,d;
	cin>>a>>b>>c>>d;
	int ans=0;
	if (c>a)
	{
		ans+=(c-a);
		b+=(c-a);
		a=c;
	}
	if (d>b)
	{
		ans+=(d-b);
		a+=d-b;
		b=d;
	}
	if (c<a&&d==b)
	{
		ans+=a-c;
		a=c;
	}
	if (a!=c||b!=d) cout<<"-1\n";
	else cout<<ans<<'\n';
}
signed main()
{
	IOS;cin.tie(0); 
	int T=1;
	cin>>T;
	while (T--)
	{
		BellaKira();
	}
}
