// Friendship is Magic
#pragma comment(linker, "/STACK:32000000")

#include <memory.h>
#include <string.h>
#include <cstdio>
#include <cmath>
#include <iostream>
#include <sstream>
#include <string>
#include <algorithm>
#include <vector>
#include <queue>
#include <set>
#include <map>

using namespace std;

typedef long long LL;

#define maxn 105

int n, k;
int x[maxn], y[maxn];

double sqr(double x) { return x * x; }

void do_solve() {
    scanf("%d%d", &n, &k);
    for (int i = 0; i < n; i++) scanf("%d%d", &x[i], &y[i]);
    double len = 0;
    for (int i = 0; i + 1 < n; i++) len += sqrt(sqr(x[i] - x[i+1]) + sqr(y[i] - y[i+1]));
    len *= k;
    len /= 50;
    printf("%.8lf", len);
}

int main()
{
#ifndef ONLINE_JUDGE
    freopen("input.txt", "r", stdin);
    freopen("output.txt", "w", stdout);
#endif
    do_solve();
    return 0;
}
