#include <cstdio>

const int MAXN = 110 + 1;

int n, a[MAXN];

int main() {
	scanf("%d", &n);
	for (int i = 0; i < n; i++) scanf("%d", &a[i]);
	for (int i = 0; i < n; i++) {
		for (int j = 0; j < n; j++) {
			if (i == j) continue;
			for (int k = 0; k < n; k++) {
				if (i == k || j == k) continue;
				if (a[i] == a[j] + a[k]) {
					printf("%d %d %d\n", i + 1, j + 1, k + 1);
					return 0;
				}
			}
		}
	}
	puts("-1");
	return 0;
}
