#include<bits/stdC++.h>
using namespace std;
int T;
int main(){
	ios::sync_with_stdio(0);
	cin>>T;
	while(T--){
		int a,b,n,m;
		cin>>a>>b>>n>>m;
		if(1ll*a*m>b*(m+1ll))
			cout<<1ll*b*n<<'\n';
		else if(a<b)
			cout<<1ll*a*(n-n/(m+1))<<'\n';
		else cout<<1ll*a*m*(n/(m+1))+1ll*n%(m+1)*b<<'\n';
	}
}
