#include <bits/stdc++.h>
#define int long long
#define x first
#define y second
#define pb push_back
using namespace std;
using pii=pair <int,int>;
using tii=pair <pii,int>;
void solve(){
    int n;
    cin>>n;
    pii p[n+1];
    for (int i=1; i<=n; i++){
        cin>>p[i].x;
        p[i].y=i;
    }
    sort(p+1,p+n+1,greater <pii>());
    int ans[n+1];
    for (int i=1; i<=n; i++) ans[p[i].y]=i;
    for (int i=1; i<=n; i++) cout<<ans[i]<<" \n"[i==n];
}
signed main(){
	ios_base::sync_with_stdio(0); cin.tie(0);
	int t=1;
	cin>>t;
	while (t--) solve();
}
