#include <bits/stdc++.h>
#define int long long
#define i64 long long
#define pii pair <int, int> 
using namespace std;
inline int read(void) {
    int x=0,sgn=1; char ch=getchar();
    while(ch<48||57<ch) {if(ch==45)sgn=0;ch=getchar();}
    while(47<ch&&ch<58) {x=x*10+ch-48;   ch=getchar();}
    return sgn? x:-x;
}
void write(int x) {
    if(x<0) putchar('-'),x=-x;
    if(x>9) write(x/10);
    putchar(x%10+'0');
}
int n,k,a,b,D1,D2,D3;
int x[200005],y[200005];
inline int dis(int a,int b) { return abs(x[a]-x[b])+abs(y[a]-y[b]); }
inline void work() {
    cin>>n>>k>>a>>b;
    for(int i=1; i<=n; ++i) cin>>x[i]>>y[i];
    D1=dis(a,b);
    D2=D3=D1;
    for(int i=1; i<=k; ++i) D2=min(D2,dis(a,i));
    for(int i=1; i<=k; ++i) D3=min(D3,dis(b,i));
    cout<<min(D2+D3,D1)<<endl;
}   
signed main() {
    int T=read();
    while(T--) work();
	return 0;
}
