#include <bits/stdc++.h>
using namespace std;
#define int long long
void solve() {
  int x, n;
  cin >> x >> n;
  int ans = 0;
  for (int d = 1; d*d <= x; d++) {
    if (x % d) continue;
    if (d*n <= x)
      ans = max(ans, d);
    if (x/d*n <= x)
      ans = max(ans, x/d);
  }
  cout << ans << endl;
}
signed main() {
  int t;
  cin >> t;
  while (t--) solve();
}
