#include<bits/stdc++.h>
using namespace std;
int f[111111];
int m,ans;

int now;
int main()
{
	scanf("%d",&m);
	for(int i=1;i<=m;i++)
		scanf("%d",&f[i]);
	sort(f+1,f+m+1);
	now=1;
	ans=1;
	for(int i=1;i<=m;i++)
	{
		if(f[i]<now)continue;
		if(f[i]>=now)
		{
			now++;
			ans=now;
		}
	}
	cout<<ans<<endl;

	return 0;
}
