#include <cstdio>
#include <cmath>
using namespace std;

int sgn(double x) {
    if (fabs(x) < 1e-8) {
        return 0;
    }
    return x > 0 ? 1 : -1;
}

int main() {
    int r, x, y, a, b;
    scanf("%d%d%d%d%d", &r, &x, &y, &a, &b);
    double dis = (x * 1.0 - a) * (x * 1.0 - a) + (y * 1.0 - b) * (y * 1.0 - b);
    dis = sqrt(dis);
    int cnt = 0;
    while (sgn(dis) > 0) {
        dis -= 2 * r;
        cnt ++;
    }
    printf("%d\n", cnt);
    return 0;
}
