#define st first

#define endl '\n'

typedef pair < int ,int > pii;

typedef long long ll;

const long long linf = 1e18+5;
const int mod = 1e9+7;
const int logN = 17;
const int inf = 1e9;
const int N = 2e6+5;

int i, j, k, n, m, x, cnt[N], l[N], r[N], ans, le, ri, aaa = inf;;

int main(){

	scanf("%d",&n);

	memset(l, 12, sizeof l);

	FOR(i,1,n) {

		scanf("%d",&x);

		l[x] = min(l[x], i);
		r[x] = max(r[x], i);
		cnt[x]++;
		ans = max(cnt[x], ans);

	}

	FOR(i,1,N-1)
		if(cnt[i] == ans && r[i]-l[i]+1 < aaa) {
			aaa = r[i] - l[i] + 1;
			le = l[i]; 
			ri = r[i];
		}

	cout << le << ' ' << ri << endl;

    return 0;
}
