#include "bits/stdc++.h"

using namespace std;

int main() {
#ifdef HOME
    freopen("input.txt", "r", stdin);
    //freopen("output.txt", "w", stdout);
#else

#endif

    string s;
    cin >> s;

    for (int i = 0; i + 1 < (int)s.length(); ++i) {
        if ((s[i] - '0') % 2 == 0 && s[i] < s[s.length() - 1]) {
            swap(s[i], s[s.length() - 1]);
            cout << s << endl;
            return 0;
        }
    }

    for (int i = (int)s.length() - 2; i >= 0; --i) {
        if ((s[i] - '0') % 2 == 0) {
            swap(s[i], s[s.length() - 1]);
            cout << s << endl;
            return 0;
        }
    }

    cout << "-1\n";

    return 0;
}
