#include <iostream>

using namespace std;

int n;
int a[110000];
int last[110000];

int main() {
    cin >> n;
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }
    for (int i = 0; i <= 100000; i++)  {
        last[i] = -1;
    }
    int l = 0;
    int ans = 0;
    for (int i = 0; i < n; i++) {
        int x = -1;
        if (a[i] + 2 <= 100000) {
            x = max(x, last[a[i] + 2]);
        }
        if (a[i] - 2 >= 0) {
            x = max(x, last[a[i] - 2]);
        }
        l = max(l, x + 1);
        ans = max(ans, i - l + 1);

        last[a[i]] = i;
    }
    cout << ans << endl;
    return 0;
}
