#define ll long long
#define sqr(x) (ll)(x)*(x)
#define dist(x,y,xx,yy) (sqr(x-xx)+sqr(y-yy))
using namespace std;

int main(){
	int x1,x2,x3,y1,y2,y3;
	scanf("%d%d%d%d%d%d",&x1,&y1,&x2,&y2,&x3,&y3);
	if ((ll)(x2-x1)*(y3-y1)==(ll)(y2-y1)*(x3-x1)){
		puts("No"); return 0;
	}
	puts((dist(x1,y1,x2,y2)==dist(x2,y2,x3,y3))?"Yes":"No");
	return 0;
}
