#include <bits/stdc++.h>
using namespace std;
typedef long long i64;
const int MAX = 1e6 + 10;
const int MOD = 1e9 + 7;

//#define acm_local
int ar[MAX];
int main()
{


#ifdef acm_local
    freopen("data.in", "r", stdin);
    return 0;
#else
    std::ios::sync_with_stdio(false);
    cin.tie(0);
#endif


    int n; cin >> n;
    int x; cin >> x;
    unordered_map<int, int> M;
    for(int i = 0; i < n; i++){
        cin >> ar[i];
        M[ar[i]]++;
    }
    i64 ans = 0;
    for(int i = 0; i < n; i++){
        int a = x ^ ar[i];
        if(a == ar[i])ans += M[ar[i]] - 1;
        else ans += M[a];
    }
    cout << ans / 2 << endl;





}
