#include <bits/stdc++.h>
#define mod 1000000007
#define int long long
using namespace std;
map <int,int> mp;
signed main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin >> T;
	while(T--)
	{
		int n;
		cin >> n;
		int mx=0;
		for(int i=1;i<=n;i++)
		{
			int x;
			cin >> x;
			mx=max(mx,++mp[x]);
		}
		mp.clear();
		int ans=n-mx;
		while(mx<n) ++ans,mx*=2;
		cout << ans << "\n";
	}
	return 0;
}
