#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <vector>
#include <iostream>
#include <algorithm>

using namespace std;

#define foreach(e,x) for(__typeof(x.begin()) e=x.begin(); e!=x.end(); ++e)

const int N = 100000;

int n, m;
int f[N][10];
int g[N][10];

void solve()
{
	cin >> m >> n;
	for(int i = 0; i < m; ++ i) {
		for(int j = 0; j < n; ++ j) {
			scanf("%d", &f[i][j]);
		}
	}
	for(int i = 0; i < m; ++ i) {
		g[i + 1][0] = g[i][0] + f[i][0];

		for(int j = 1; j < n; ++ j) {
			g[i + 1][j] = max(g[i + 1][j - 1] + f[i][j], g[i][j] + f[i][j]);
		}

		printf("%d ", g[i + 1][n - 1]);
	}
}

int main()
{
	solve();
	return 0;
}
