using namespace std;
int n,M,sum = 0,minTime[100],maxTime[100];
int main()
{ 
//    freopen("test.in","r",stdin);
//    freopen("test.out","w",stdout);
    cin >> n >> M;
    FOR(i,1,n)
    {
        scanf("%d%d",&minTime[i],&maxTime[i]);
        maxTime[i] -= minTime[i];
        M -= minTime[i];
        sum += maxTime[i];
    }
    if (M < 0)
    {
        cout << "NO";
        return 0;
    }
    if (sum < M)
    {
        cout << "NO";
        return 0;
    }
    cout << "YES" << endl;    
    FOR(i,1,n)
    {
        if (maxTime[i] <= M)
        {
            M -= maxTime[i];
            cout << maxTime[i] + minTime[i] << " ";
        } else
        {
            cout << minTime[i] + M << " ";
            M = 0;
        }
    }
    return 0;
}
