#include<bits/stdc++.h>
using namespace std;

typedef long long ll;
typedef pair<int, int> pii;
const int N = 202;

int n, a[N], cnt[2];
string s;
vector<int> vec;

int main() {
	ios::sync_with_stdio(false), cin.tie(0);
	cin >> n >> s;
	for (int i = 0; i < n; i++)
		cnt[a[i] = (s[i] == 'W')]++;
	if (cnt[0] % 2 && cnt[1] % 2) {
		cout << -1;
		return 0;
	}
	for (int i = 1; i < n - 1; i++)
		if (a[i] != a[i - 1]) {
			vec.push_back(i);
			a[i] ^= 1;
			a[i + 1] ^= 1;
		}
	for (int i = n - 2; i; i--)
		if (a[i] != a[i + 1]) {
			vec.push_back(i - 1);
			a[i] ^= 1;
			a[i - 1] ^= 1;
		}

	cout << vec.size() << '\n';
	for (int x: vec)
		cout << ++x << ' ';
}
